package basics;

public class Star {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int tCol = 5;
		for(int col=5;col>=tCol;col--)
		{
			
			for(int row=1;row<=col;row++)
			
			{
				System.out.print(" * ");
			}
			System.out.println();
		}




}



}


